import React, { PureComponent } from 'react'
import {ProductConsumer} from './Context'
import {Link} from 'react-router-dom'


class Product extends PureComponent {
    constructor(props) {
        super(props)

        this.state = {
            
        }
    }

    

    render() {

        const styletit={
            position:"relative",
            backgroundColor:"grey"
        }
        const stylecart={
            position:"absolute",
            backgroundColor:"grey",
            bottom:0
        }

    
       const {id, title, img,price, inCart} =this.props.product
        return (
            
            <div>
               
                <div className="container">
                    <div class="card pb-0  mb-3">
                        <div>
                                <Link to="/details">
                                    <img src={img} alt="image" className="card-img-top"/>
                                </Link>

                                <button className="cart-btn" style={stylecart} disabled={inCart ? true:false}>

                                      {inCart?(<p className="mb-0 p-1" disabled>In cart</p>) : (<i className="fas fa-cart-plus p-2"></i>)}
                                </button>
                        </div>
                        <div className="card-footer d-flex justify-content-between mb-0" style={styletit}>
                            <p className="mb-0">{title}</p>
                            <h4><span className="mr-1">$</span>{price}</h4>
                        </div>
                    </div>
                </div>
            </div>
            
        )
    }
}

export default Product
