import React, { PureComponent } from 'react'
import {Link} from 'react-router-dom'
import {ProductConsumer} from './Context'
import '../App.css'

class Product extends PureComponent {
    
    render() {

        const styletit={
            backgroundColor:"grey"
        }
        const stylecart={
            position:"absolute",
            backgroundColor:"grey",
            bottom:0,right:0,
            padding:4,margin:2,
            transform:"translate( 100%,100%)",
            

        }


        const styleimg={
            position:"relative",
            
            
        }

        

       const {id, title, img,price, inCart} =this.props.product
        return (
            
            <div>
               
                <div className="container">
                    <div className="card pb-0  mb-3">
                       <ProductConsumer>

                           {value =>{

                                  return(<div style={styleimg} onClick={()=> value.handleDetail(id)} >
                                  <Link to="/details">
                                      <img src={img} alt="xxx" className="card-img-top" />
                                  </Link>

                              <button  style={stylecart} className="hover" disabled={inCart} onClick={()=> value.addToCart(id)}>

                                  {inCart?(<p className="mb-0 p-1" disabled>In cart</p>) : (<i className="fas fa-cart-plus p-2"></i>)}
                              </button>
                              
                              </div>)  
                           }}
                       </ProductConsumer>
                        
                        <div className="card-footer d-flex justify-content-between mb-0" style={styletit}>
                            <p className="mb-0">{title}</p>
                            <h4><span className="mr-1">$</span>{price}</h4>
                        </div>
                    </div>
                </div>
            </div>
            
        )
    }
}

export default Product
