        return (
           <ProductConsumer>
               {
                   (value) =>{

                    const {id,company,img,info,price,title,inCart}= value.detailProduct

                    return(
                        <div className="container py-5">
                            <div className="row ">
                                <div className="mx-auto py-5">


                                    <h3>{title}</h3>

                                </div>
                                <div>
                                    <img src={img} alt="title" />
                                </div>
                            </div>
                        </div>
                    )

                   }
               }

           </ProductConsumer>
            
        )
    }
}

export default Details
