        }
    }

    

    render() {

        const stylee={
            color:"red",
            textAlign:"center",
            padding:15
        }
        const parserConfig = {
            replace: domNode => {
                if(domNode.type === DOM_TYPE_TAG && domNode.name === 'img') {
                    return <img src={domNode.attribs.src} alt={domNode.attribs.alt} className={domNode.attribs.class} />;
                }
            }
        };

       const {id, title, img,price, inCart} =this.props
        return (

            <div>
                <div><h2 className="prod" style={stylee} >Our Products</h2></div>

                <div className="container">
                    <div class="card">
                        <div>
                                <Link to="/details">
                                    <img src={img} alr="image"> </img>
                                </Link>
                        </div>
                    </div>
                </div>
            </div>
            
        )
    }
}

export default Product
